import React, { useState, useEffect } from 'react';
import { io, Socket } from 'socket.io-client';
import './App.css';

interface User {
  id: number;
  username: string;
  chips: number;
}

interface GameState {
  players: {
    id: number;
    username: string;
    chips: number;
    cards: string[];
    bet: number;
    isActive: boolean;
    isFolded: boolean;
  }[];
  communityCards: string[];
  pot: number;
  currentPlayer: number;
  dealer: number;
  smallBlind: number;
  bigBlind: number;
  currentBet: number;
  phase: 'preflop' | 'flop' | 'turn' | 'river' | 'showdown';
}

function App() {
  const [socket, setSocket] = useState<Socket | null>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [user, setUser] = useState<User | null>(null);
  const [gameState, setGameState] = useState<GameState | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isSignUp, setIsSignUp] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      verifyToken(token);
    }
  }, []);

  const verifyToken = async (token: string) => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/verify', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        connectToGame(data.user);
      } else {
        localStorage.removeItem('token');
      }
    } catch (err) {
      console.error('Token verification failed:', err);
      localStorage.removeItem('token');
    }
  };

  const connectToGame = (user: User) => {
    const newSocket = io('http://localhost:5000', {
      auth: {
        token: localStorage.getItem('token')
      }
    });

    newSocket.on('connect', () => {
      console.log('Connected to game server');
      setIsConnected(true);
      setError(null);
    });

    newSocket.on('disconnect', () => {
      console.log('Disconnected from game server');
      setIsConnected(false);
    });

    newSocket.on('error', (error: string) => {
      console.error('Game server error:', error);
      setError(error);
    });

    newSocket.on('gameState', (state: GameState) => {
      console.log('Received game state:', state);
      setGameState(state);
    });

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  };

  const handleAuth = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);

    try {
      const endpoint = isSignUp ? '/api/auth/signup' : '/api/auth/signin';
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Authentication failed');
      }

      localStorage.setItem('token', data.token);
      setUser(data.user);
      connectToGame(data.user);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Authentication failed');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignOut = () => {
    localStorage.removeItem('token');
    setUser(null);
    if (socket) {
      socket.close();
      setSocket(null);
    }
  };

  const handleAction = (action: string, amount?: number) => {
    if (!socket || !gameState) return;

    socket.emit('action', { action, amount });
  };

  if (!user) {
    return (
      <div className="auth-container">
        <div className="auth-box">
          <h2>{isSignUp ? 'Sign Up' : 'Sign In'}</h2>
          {error && <div className="error">{error}</div>}
          <form onSubmit={handleAuth}>
            <div className="form-group">
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" disabled={isLoading}>
              {isLoading ? 'Loading...' : isSignUp ? 'Sign Up' : 'Sign In'}
            </button>
          </form>
          <button
            className="switch-auth"
            onClick={() => setIsSignUp(!isSignUp)}
          >
            {isSignUp ? 'Already have an account? Sign In' : 'Need an account? Sign Up'}
          </button>
        </div>
      </div>
    );
  }

  if (!gameState) {
    return (
      <div className="loading">
        <h2>Loading game...</h2>
        <button onClick={handleSignOut}>Sign Out</button>
      </div>
    );
  }

  const currentPlayer = gameState.players.find(p => p.id === user.id);
  const isPlayerTurn = currentPlayer?.id === gameState.currentPlayer;

  return (
    <div className="game-container">
      <div className="game-header">
        <h2>Texas Hold'em Poker</h2>
        <div className="user-info">
          <span>Welcome, {user.username}</span>
          <span>Chips: {user.chips}</span>
          <button onClick={handleSignOut}>Sign Out</button>
        </div>
      </div>

      <div className="game-table">
        <div className="community-cards">
          {gameState.communityCards.map((card, index) => (
            <div key={index} className="card">
              {card}
            </div>
          ))}
        </div>

        <div className="pot">
          Pot: {gameState.pot}
        </div>

        <div className="players">
          {gameState.players.map((player) => (
            <div
              key={player.id}
              className={`player ${player.id === gameState.currentPlayer ? 'active' : ''} ${player.id === user.id ? 'self' : ''}`}
            >
              <div className="player-info">
                <span>{player.username}</span>
                <span>Chips: {player.chips}</span>
                {player.bet > 0 && <span>Bet: {player.bet}</span>}
              </div>
              <div className="player-cards">
                {player.cards.map((card, index) => (
                  <div key={index} className="card">
                    {player.id === user.id ? card : '🂠'}
                  </div>
                ))}
              </div>
              {player.isFolded && <div className="folded">Folded</div>}
            </div>
          ))}
        </div>

        {isPlayerTurn && (
          <div className="actions">
            <button onClick={() => handleAction('fold')}>Fold</button>
            <button onClick={() => handleAction('check')}>Check</button>
            <button onClick={() => handleAction('call')}>Call</button>
            <button onClick={() => handleAction('raise', gameState.currentBet * 2)}>
              Raise
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
