import React, { useState, useEffect } from 'react';
import { io, Socket } from 'socket.io-client';
import './App.css';

interface User {
  id: number;
  username: string;
  chips: number;
}

interface GameState {
  players: {
    id: number;
    username: string;
    chips: number;
    cards: string[];
    bet: number;
    isActive: boolean;
    isFolded: boolean;
    isDealer: boolean;
    isSmallBlind: boolean;
    isBigBlind: boolean;
    lastAction?: string;
  }[];
  communityCards: string[];
  pot: number;
  currentPlayer: number;
  dealer: number;
  smallBlind: number;
  bigBlind: number;
  currentBet: number;
  minimumRaise: number;
  phase: 'waiting' | 'preflop' | 'flop' | 'turn' | 'river' | 'showdown';
  lastAction?: string;
  lastActionAmount?: number;
  minPlayers: number;
  maxPlayers: number;
  votes?: number[];
}

function App() {
  const [socket, setSocket] = useState<Socket | null>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [user, setUser] = useState<User | null>(null);
  const [gameState, setGameState] = useState<GameState | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isSignUp, setIsSignUp] = useState(false);
  const [raiseAmount, setRaiseAmount] = useState(0);
  const [showCards, setShowCards] = useState(false);
  const [showRaiseSlider, setShowRaiseSlider] = useState(false);
  const [showInstructions, setShowInstructions] = useState(false);
  const [votes, setVotes] = useState<number[]>([]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      verifyToken(token);
    }
  }, []);

  const verifyToken = async (token: string) => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/verify', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        connectToGame(data.user);
      } else {
        localStorage.removeItem('token');
      }
    } catch (err) {
      console.error('Token verification failed:', err);
      localStorage.removeItem('token');
    }
  };

  const connectToGame = (user: User) => {
    const newSocket = io('http://localhost:5000', {
      auth: {
        token: localStorage.getItem('token')
      }
    });

    newSocket.on('connect', () => {
      console.log('Connected to game server');
      setIsConnected(true);
      setError(null);
      newSocket.emit('joinGame', user.username);
    });

    newSocket.on('disconnect', () => {
      console.log('Disconnected from game server');
      setIsConnected(false);
    });

    newSocket.on('error', (error: string) => {
      console.error('Game server error:', error);
      setError(error);
    });

    newSocket.on('gameState', (state: GameState) => {
      console.log('Received game state:', state);
      setGameState(state);
      setVotes(state.votes || []);
      if (state.phase === 'showdown') {
        setShowCards(true);
        setTimeout(() => setShowCards(false), 3000);
      }
    });

    newSocket.on('voteStatus', (votes: number[]) => {
      setVotes(votes);
    });

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  };

  const handleAuth = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);

    try {
      const endpoint = isSignUp ? '/api/auth/signup' : '/api/auth/signin';
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Authentication failed');
      }

      localStorage.setItem('token', data.token);
      setUser(data.user);
      connectToGame(data.user);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Authentication failed');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignOut = () => {
    localStorage.removeItem('token');
    setUser(null);
    if (socket) {
      socket.close();
      setSocket(null);
    }
  };

  const handleAction = (action: string, amount?: number) => {
    if (!socket || !gameState) return;

    if (action === 'raise') {
      if (!amount || amount < gameState.minimumRaise) {
        setError(`Minimum raise is ${gameState.minimumRaise}`);
        return;
      }
    }

    socket.emit('action', { action, amount });
    setShowRaiseSlider(false);
  };

  const handleRaiseSlider = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (!gameState) return;
    const value = parseInt(e.target.value);
    setRaiseAmount(value);
  };

  const getCardColor = (card: string) => {
    return card.includes('‚ô•') || card.includes('‚ô¶') ? 'red' : 'black';
  };

  const getPlayerPosition = (player: GameState['players'][0]) => {
    const positions = [];
    if (player.isDealer) positions.push('D');
    if (player.isSmallBlind) positions.push('SB');
    if (player.isBigBlind) positions.push('BB');
    return positions.join(' ');
  };

  const renderWaitingRoom = () => {
    if (!gameState) return null;
    return (
      <div className="waiting-room">
        <div className="waiting-info">
          <h2>Waiting for Players</h2>
          <div className="player-count">
            {gameState.players.length} / {gameState.maxPlayers} Players
          </div>
          <div className="min-players">
            Game starts with {gameState.minPlayers} players
          </div>
          <div className="table-info">
            <div className="info-item">
              <span className="label">Small Blind:</span>
              <span className="value">$5</span>
            </div>
            <div className="info-item">
              <span className="label">Big Blind:</span>
              <span className="value">$10</span>
            </div>
            <div className="info-item">
              <span className="label">Starting Stack:</span>
              <span className="value">$1000</span>
            </div>
          </div>
        </div>
        <div className="join-instructions">
          <button 
            className="instructions-button"
            onClick={() => setShowInstructions(!showInstructions)}
          >
            How to Join
          </button>
          {showInstructions && (
            <div className="instructions-content">
              <h3>How to Join the Game:</h3>
              <ol>
                <li>Open a new browser window or incognito mode</li>
                <li>Go to <code>http://localhost:3000</code></li>
                <li>Create a new account or sign in</li>
                <li>You'll automatically join the waiting room</li>
                <li>Game starts when {gameState.minPlayers} players join</li>
              </ol>
              <div className="share-link">
                <p>Share this link with friends:</p>
                <code>http://localhost:3000</code>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  };

  const handleVoteToStart = () => {
    if (socket) {
      socket.emit('voteToStart');
    }
  };

  if (!user) {
    return (
      <div className="auth-container">
        <div className="auth-box">
          <div className="auth-logo">‚ô†Ô∏è ‚ô•Ô∏è ‚ô£Ô∏è ‚ô¶Ô∏è</div>
          <h2>{isSignUp ? 'Create Account' : 'Welcome Back'}</h2>
          {error && <div className="error">{error}</div>}
          <form onSubmit={handleAuth}>
            <div className="form-group">
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" disabled={isLoading}>
              {isLoading ? 'Loading...' : isSignUp ? 'Create Account' : 'Sign In'}
            </button>
          </form>
          <button
            className="switch-auth"
            onClick={() => setIsSignUp(!isSignUp)}
          >
            {isSignUp ? 'Already have an account? Sign In' : 'Need an account? Sign Up'}
          </button>
        </div>
      </div>
    );
  }

  if (!gameState) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
        <h2>Loading game...</h2>
        <button onClick={handleSignOut}>Sign Out</button>
      </div>
    );
  }

  if (gameState.phase === 'waiting') {
    return (
      <div className="game-container">
        <div className="game-header">
          <div className="game-title">
            <h2>Texas Hold'em Poker</h2>
          </div>
          <div className="user-info">
            <div className="user-stats">
              <span className="username">{user.username}</span>
              <span className="chips">${user.chips}</span>
            </div>
            <button className="sign-out" onClick={handleSignOut}>Sign Out</button>
          </div>
        </div>
        {renderWaitingRoom()}
        {gameState.players.length >= 2 && (
          <div className="waiting-vote-room">
            <div className="waiting-info">
              <h2>Waiting for Players</h2>
              <div className="player-count">
                {gameState.players.length} / {gameState.maxPlayers} Players
              </div>
              <div className="min-players">
                Game starts with {gameState.minPlayers} players
              </div>
            </div>
            <div className="vote-section">
              <button
                className="vote-to-start-btn"
                onClick={handleVoteToStart}
                disabled={votes.includes(user.id)}
              >
                {votes.includes(user.id) ? 'Voted!' : 'Vote to Start'}
              </button>
              <div className="vote-status">
                <strong>Votes:</strong>
                <ul>
                  {gameState.players.map((p) => (
                    <li key={p.id} style={{ color: votes.includes(p.id) ? '#4caf50' : '#fff' }}>
                      {p.username} {votes.includes(p.id) ? '‚úÖ' : ''}
                    </li>
                  ))}
                </ul>
                <div>
                  {votes.length} / {gameState.players.length} voted
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  }

  const currentPlayer = gameState.players.find(p => p.id === user.id);
  const isPlayerTurn = currentPlayer?.id === gameState.currentPlayer;
  const callAmount = gameState.currentBet - (currentPlayer?.bet || 0);

  return (
    <div className="game-container">
      <div className="game-header">
        <div className="game-title">
          <h2>Texas Hold'em Poker</h2>
          <div className="game-phase">{gameState.phase.toUpperCase()}</div>
        </div>
        <div className="user-info">
          <div className="user-stats">
            <span className="username">{user.username}</span>
            <span className="chips">${user.chips}</span>
          </div>
          <button className="sign-out" onClick={handleSignOut}>Sign Out</button>
        </div>
      </div>

      <div className="game-table">
        <div className="table-info">
          <div className="pot">Pot: ${gameState.pot}</div>
          {gameState.lastAction && (
            <div className="last-action">
              {gameState.lastAction} {gameState.lastActionAmount ? `$${gameState.lastActionAmount}` : ''}
            </div>
          )}
        </div>

        <div className="community-cards">
          {gameState.communityCards.map((card, index) => (
            <div key={index} className={`card ${getCardColor(card)}`}>
              {card}
            </div>
          ))}
        </div>

        <div className="players">
          {gameState.players.map((player) => (
            <div
              key={player.id}
              className={`player ${player.id === gameState.currentPlayer ? 'active' : ''} ${player.id === user.id ? 'self' : ''}`}
            >
              <div className="player-info">
                <div className="player-header">
                  <span className="player-name">{player.username}</span>
                  <span className="player-position">{getPlayerPosition(player)}</span>
                </div>
                <div className="player-stats">
                  <span className="chips">${player.chips}</span>
                  {player.bet > 0 && <span className="bet">Bet: ${player.bet}</span>}
                </div>
                {player.lastAction && (
                  <div className="player-action">{player.lastAction}</div>
                )}
              </div>
              <div className="player-cards">
                {player.cards.map((card, index) => (
                  <div 
                    key={index} 
                    className={`card ${getCardColor(card)} ${player.id === user.id && !showCards ? 'hidden' : ''}`}
                  >
                    {player.id === user.id && !showCards ? 'üÇ†' : card}
                  </div>
                ))}
              </div>
              {player.isFolded && <div className="folded">FOLDED</div>}
            </div>
          ))}
        </div>

        {isPlayerTurn && (
          <div className="actions">
            <button 
              className="action-button fold"
              onClick={() => handleAction('fold')}
            >
              Fold
            </button>
            <button 
              className="action-button check"
              onClick={() => handleAction('check')}
              disabled={callAmount > 0}
            >
              Check
            </button>
            <button 
              className="action-button call"
              onClick={() => handleAction('call')}
              disabled={callAmount === 0}
            >
              Call ${callAmount}
            </button>
            <button 
              className="action-button raise"
              onClick={() => setShowRaiseSlider(!showRaiseSlider)}
            >
              Raise
            </button>
            {showRaiseSlider && (
              <div className="raise-controls">
                <input
                  type="range"
                  min={gameState.minimumRaise}
                  max={currentPlayer?.chips || 0}
                  value={raiseAmount}
                  onChange={handleRaiseSlider}
                  className="raise-slider"
                />
                <div className="raise-amount">${raiseAmount}</div>
                <button 
                  className="action-button confirm-raise"
                  onClick={() => handleAction('raise', raiseAmount)}
                >
                  Raise to ${raiseAmount}
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
